var lastPart = function($url){
	var url = $url;
	// Split URL into parts and store them as array 
	var parts = url.split("/");
	// Get value of last part
	var last_part = parts[parts.length - 1];
	// In case there is a '/' at the end
	if(last_part === '') last_part = parts[parts.length - 2];
		return last_part;
}
	
